/**
 * swift 枚举类型
 */

// 声明枚举 下面两种方法等价
enum Method {
//  case Add
//  case Sub
//  case Mul
//  case Div
  case Add, Sub, Mul, Div
}

// 使用枚举的两种写法
var m0: Method = .Add
var m1 = Method.Sub

// switch匹配枚举
func chooseMethod(op: Method) -> (Double, Double) -> Double {
  switch op {
    case .Add:
      func add(a:Double, b:Double) -> Double {
        return a + b
      }
      return add
    case .Sub:
      return {
        (a:Double, b:Double) -> Double in
        return a - b
      }
    case .Mul:
      return {
        return $0 * $1
      }
    case .Div:
      return {
        $0 / $1
      }
//  default:
//    return { $0 / $1}
  }
}

let val = chooseMethod(.Mul)(30, 5)

// 枚举的原始值
