    var m1 = Method.Sub

    // switch匹配枚举
    func chooseMethod(op: Method) -> (Double, Double) -> Double {
      switch op {
        case .Add:
          func add(a:Double, b:Double) -> Double {
            return a + b
          }
          return add
        case .Sub:
          return {
            (a:Double, b:Double) -> Double in
            return a - b
          }
        case .Mul:
          return {
            return $0 * $1
          }
        case .Div:
          return {
            $0 / $1
          }
    //  default:
